                    "value": {
                        "type": "number",
                        "description": "The numeric value to convert",
                    },
                    "from_unit": {
                        "type": "string",
                        "enum": ["m", "cm", "km", "kg", "g", "l", "ml"],
                        "description": "The original unit of measurement",
                    },
